mapper.select:
SELECT id,
good_id,
brand_id,
banner_id,
id_photo_owner_id,
article_id,
activity_id,
estimate_id,
is_rich_text,
create_date,
url
FROM t_mall_image WHERE 1=1
AND good_id=#{targetId}
AND brand_id=#{targetId}
AND article_id=#{targetId}
AND estimate_id=#{targetId}
AND banner_id=#{targetId}
AND activity_id=#{targetId}
AND is_rich_text='NORMAL'
AND id_photo_owner_id =#{targetId}
AND id_photo_owner_id IS NOT NULL

mapper.update:
UPDATE t_mall_image SET update_date=now()
,create_date=#{createDate}
,url=#{url}
,article_id=#{articleId}
,activity_id=#{activityId}
,banner_id=#{bannerId}
,brand_id=#{brandId}
,good_id=#{goodId}
,estimate_id=#{estimateId}
,is_rich_text=#{isRichText}
,id_photo_owner_id=#{idPhotoOwnerId}
WHERE id=#{id}
